INSERT INTO VENUE (ID,ACCESS,CONTENT,FACILITIES,IMAGE_ID,LATITUDE,LONGTITUDE,NAME) VALUES(1, 'h4. By public transport:\n\nh5. Tube Stations:\n\n* Kennington (5 min walk) \n* Elephant and Castle\n\nh5. Buses:\n\n* No. 148\n* No. 176\n* No. 12\n* No N35\n* No N68\n* No N89\n* No N171\n\n\nThere is limited spaces for parking.', 'Kagyu Samye Dzong London offers a unique place for people to unwind from the stressful pace of inner city life. The many meditation classes and more specialised courses are offered at low cost and the use of the shrine room for personal practice as well as group meditation is free of charge. With regular meditations and prayers, the activities of the centres are dedicated to peace, harmony and happines in the world.\n\nOur second centre is situated just off Walworth Road at Manor Place\n\nOPENING TIMES:\n\n* Monday the Centre is closed.\n* Tuesday the Centre is closed.\n* Wednesday 2pm - 9pm\n* Thursday 12noon - 9pm\n* Friday 9am - 9pm\n* Saturday 2pm - 6pm \n* Sunday 2pm - 6pm\n','Shrine Room, Kitchen, Library and more',0,51.488613000,'-0.098543000','Manor Place'); 
INSERT INTO VENUE (ID,ACCESS,CONTENT,FACILITIES,IMAGE_ID,LATITUDE,LONGTITUDE,NAME) VALUES(2, 'h4. By public transport:\n\nh5. Tube Stations:\n\nThe nearest tube station is Bermondsey on the Jubilee Line.\n\nh5. Buses\n\nThese buses come near the centre:\n\n* No 1\n* No 78\n* No 188\n* No 42\n* No 47\n* No 381\n* No C10\n', 'OPENING TIMES:\n\n* Monday the Centre is closed.\n* Tuesday the Centre is closed.\n* Wednesday 2.00pm - 9.00pm\n* Thursday 5.00pm - 9.00pm\n* Friday 5.00pm - 9.00pm\n* Saturday 2.00pm - 9.00pm \n* Sunday 2.00pm - 9.00pm\n','* Library\n* Shop\n* Shrine Room',0,51.495450000,-0.074664000,'Spa Road');
